function sortReviews(e){const t=document.getElementById("reviewsList"),n=Array.from(t.children);n.sort((t,n)=>{if("high-low"===e||"low-high"===e){const r=parseInt(t.querySelector(".number").textContent),s=parseInt(n.querySelector(".number").textContent);return"high-low"===e?s-r:r-s}if("newest"===e){const e=new Date(t.dataset.releaseDate),r=new Date(n.dataset.releaseDate);return r-e}}),n.forEach(e=>t.appendChild(e))}function filterReviews(e){const t=document.getElementById("reviewsList"),n=Array.from(t.children);n.forEach(t=>{const n=t.querySelector(".review-title").textContent.toLowerCase(),r=t.dataset.platform.toLowerCase();n.includes(e)||r.includes(e)?t.style.display="":t.style.display="none"})}document.getElementById("sortBy").addEventListener("change",e=>{sortReviews(e.target.value)}),document.getElementById("searchInput").addEventListener("input",e=>{filterReviews(e.target.value.toLowerCase())}),sortReviews("high-low");